void test_poly_split_polynomial_custom(){
    uint8_t e[2*R_SIZE]={0};
    
    uint8_t e0_ref[R_SIZE]={0};
    uint8_t e1_ref[R_SIZE]={0};

    uint8_t e0_res[R_SIZE]={0};
    uint8_t e1_res[R_SIZE]={0};

    srand((unsigned)time(NULL));
    for(int i=0;i<(2*R_SIZE);i++){
        e[i]=rand()&255;
    }

    poly_split_polynomial(e0_ref,e1_ref,e);
    poly_split_polynomial_custom(e0_res,e1_res,e);

    bool flag=true;
    for(int i=0;i<R_SIZE;i++){
        if(e0_ref[i]!=e0_res[i]){
            flag=false;
            printf("e0_ref[%d]=%d,e0_res[%d]=%d\n",i,e0_ref[i],i,e0_res[i]);
        }
    }
    printf("\n");
    for(int i=0;i<R_SIZE;i++){
        if(e1_ref[i]!=e1_res[i]){
            flag=false;
            printf("e1_ref[%d]=%d,e1_res[%d]=%d\n",i,e1_ref[i],i,e1_res[i]);
        }
    }
    if(flag)printf("success in test_poly_split_polynomial_custom");
    else printf("fail in test_poly_split_polynomial_custom");
    return;
}

void test_poly_inv_binary_custom8(){
    uint8_t h0[R_SIZE]={0};
    for(int i=0;i<DV;i++){
        uint16_t idx=(((uint32_t)rand()<<16)|(uint32_t)rand())%R_BITS;
        SET_BIT(h0,idx);
    }
    uint8_t inv_h0_ref[R_SIZE]={0};
    uint8_t inv_h0_res[R_SIZE]={0};

    poly_mod_inv(inv_h0_ref,h0);
    poly_inv_binary_custom8(inv_h0_res,h0);

    bool flag=true;
    for(int i=0;i<R_SIZE;i++){
        if(inv_h0_ref[i]!=inv_h0_res[i]){
            flag=false;
            break;
        }
    }
    if(flag)printf("success in test_poly_inv_binary_custom8");
    else printf("fail in test_poly_inv_binary_custom8");
    return;
}

